def string_to_letter_freq(string):
    obs = np.zeros(N_CHARACTERS) # empty vector for observed freqs
    n_letters = len(string)/BITS_PER_LETTER
    for i in range(n_letters):
        this_letter = string[i*BITS_PER_LETTER:(i+1)*BITS_PER_LETTER]
        obs[CHARACTER_MAPS[this_letter]] += 1
    return obs

